import discord from discord import app_commands
import ezcord
bot = ezcord.Bot()
class Dropdown(ezcord.DropdownPaginator): def __init__(self, options: list[discord.SelectOption]): super().__init__(options=options)
async def callback(self, interaction: discord.Interaction): await super().callback(interaction) await interaction.response.send_message("Item or page selected: " + self.values[0])
if self.item_selected: await interaction.followup.send("Item selected: " + self.values[0])
@bot.tree.command async def $COMMAND$(interaction: discord.Interaction): options = [discord.SelectOption(label=f"Item {i}") for i in range($ITEM_COUNT$)] view = discord.ui.View(Dropdown(options)) await interaction.response.send_message(view=view)
if __name__ == "__main__": bot.run() $END$
